$(".collapse-box-tool").click(function() {
    var o = $(this).closest("div.collapse-box")
        , e = $(this).find("i")
        , i = o.find("div.collapse-box-content");
    i.slideToggle(200),
        e.toggleClass("fa-plus").toggleClass("fa-minus"),
        setTimeout(function() {
            o.resize(),
            o.find("[id^=map-]").resize()
        }, 50)
});